def find_s(lst):
    min_val = min(lst[50:])
    sm = float('inf')
    smi = None

    for i, num in enumerate(lst[50:]):
        if num != min_val and num < sm:
            sm = num
            smi = i + 50
    return sm, smi

sm, smi = find_s(y)
smi = y.index(min(y[125:]))

smin_point = (x[smi], y[smi])

